mapper.select:
select max(rb.end_time) as end_time
from receivable_bill rb
where rb.cp_unit_id = #{qe.cpUnitId}
  and rb.sup_cp_unit_id = #{qe.supCpUnitId}
  and rb.charge_item_id = #{qe.chargeItemId}
  and rb.state in (0, 1)
union
select max(ab.end_time) as end_time
from advance_bill ab
where ab.cp_unit_id = #{qe.cpUnitId}
  and ab.sup_cp_unit_id = #{qe.supCpUnitId}
  and ab.charge_item_id =  #{qe.chargeItemId}
  and ab.state in (0, 1)
